contracts/vault-types/UniLiquidityManage.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.7.6;
pragma abicoder v2;
// ERC support
import { ERC20Upgradeable } from "@openzeppelin/contracts-upgradeable-7/token/ERC20/ERC20Upgradeable.sol";
import { SafeERC20 } from "@openzeppelin/contracts-7/token/ERC20/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts-7/token/ERC20/IERC20.sol";

// Math
import { Math } from "@openzeppelin/contracts-7/math/Math.sol"; // max(), min(), and average
import { SafeMath } from "@openzeppelin/contracts-7/math/SafeMath.sol";

// Uniswap
import { IUniswapV3Factory } from "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import { IUniswapV3Pool } from "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import { TickMath } from "@uniswap/v3-core/contracts/libraries/TickMath.sol";
import { SqrtPriceMath } from "@uniswap/v3-core/contracts/libraries/SqrtPriceMath.sol";
import { FullMath } from "@uniswap/v3-core/contracts/libraries/FullMath.sol";
import { IUniswapV3MintCallback } from "@uniswap/v3-core/contracts/interfaces/callback/IUniswapV3MintCallback.sol";
import { IUniswapV3SwapCallback } from "@uniswap/v3-core/contracts/interfaces/callback/IUniswapV3SwapCallback.sol";
import { LiquidityAmounts } from "@uniswap/v3-periphery/contracts/libraries/LiquidityAmounts.sol";
import { PositionKey } from "@uniswap/v3-periphery/contracts/libraries/PositionKey.sol";

// Util support
import { PausableUpgradeable } from "@openzeppelin/contracts-upgradeable-7/utils/PausableUpgradeable.sol";
import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable-7/access/OwnableUpgradeable.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable-7/proxy/Initializable.sol";
import { AccessControlUpgradeable } from "@openzeppelin/contracts-upgradeable-7/access/AccessControlUpgradeable.sol";
import { PausableUpgradeable } from "@openzeppelin/contracts-upgradeable-7/utils/PausableUpgradeable.sol";
import { StringsUpgradeable } from "@openzeppelin/contracts-upgradeable-7/utils/StringsUpgradeable.sol";

// Sundry
import { IBareVaultRegistry } from "contracts/interfaces/IBareVaultRegistry.sol";
import { IBaseDeposit } from "contracts/interfaces/IBaseDeposit.sol";

abstract contract BaseLiquidityManager is
    Initializable,
    ERC20Upgradeable,
    PausableUpgradeable,
    AccessControlUpgradeable,
    IUniswapV3MintCallback,
    IUniswapV3SwapCallback
{
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    //Immutables and constants
    uint256 internal constant PRECISION = 1e18;

    bytes32 internal constant STEER_ROLE = keccak256("STEER_ROLE"); // Can collect Steer fees (Governance)
    bytes32 internal constant ORCHESTRATOR_ROLE =
        keccak256("ORCHESTRATOR_ROLE"); //Can call update function
    bytes32 internal constant GOVERNANCE_ROLE = keccak256("GOVERNANCE_ROLE"); // Can call update function
    //There is also access control of a sort for the strategist, who may withdraw strategy fees, but that is stored in the VaultRegistry.

    address internal vaultRegistry;

    address public token0;
    address public token1;

    // Fee info
    // Fee rates, each multiplied by 10,000 (a TOTAL_FEE of 100 means a 1% cut of total uniswap fees)
    uint24 public constant TOTAL_FEE = 1500; // Total fraction of fees not going towards LPs, multiplied by 10,000
    uint24 public constant STEER_FRACTION_OF_FEE = 6667; // Total fraction of fees going towards Steer (as opposed to going towards strategist)
    uint24 internal constant FEE_DIVISOR = 1e4;
    uint24 internal constant ONE_MINUS_FEE = FEE_DIVISOR - TOTAL_FEE;

    // Fees currently owed to Steer
    uint256 public accruedSteerFees0;
    uint256 public accruedSteerFees1;

    // Fees currently owed to strategist
    uint256 public accruedStrategistFees0;
    uint256 public accruedStrategistFees1;

    // Max # of LP tokens--users will not be allowed to deposit if that deposit would push LP tokens above max
    uint256 public maxTotalSupply;

    // Constants
    IUniswapV3Factory internal constant FACTORY =
        IUniswapV3Factory(0x1F98431c8aD98523631AE4a59f267346ea31F984);

    IUniswapV3Pool public pool;

    // For depositing
    int24 internal maxTickChange; // Roughly corresponds to a 5% diff between current price and twap price. Cannot ever be negative--simply int24 for gas efficiency.
    uint32 internal twapInterval; // Number of seconds to get the time-weighted average over

    modifier onlyRole(bytes32 _role) {
        require(hasRole(_role, msg.sender), "ROLE");
        _;
    }

    /**
     * @dev pool/vault info as of the end of a tend
     * @param sqrtPriceX96 is current pool price
     * @param totalAmount0 is the total user-owned token0, including funds held in the vault and funds deposited into the pool
     * @param totalSupply is the total number of shares in this vault.
     */
    event Snapshot(
        uint160 sqrtPriceX96,
        uint256 totalAmount0,
        uint256 totalAmount1,
        uint256 totalSupply
    );

    /**
     * @dev deposit info
     * @param sender is the address which provided the tokens
     * @param to is the receiver of vault shares from this deposit, generally the same as sender
     * @param shares is the number of shares that have been minted by this deposit
     * @param amount0 is the amount of t0 that was used to mint the shares
     * @param amount1 is the amount of t1 that was used to mint the shares
     */
    event Deposit(
        address indexed sender,
        address indexed to,
        uint256 shares,
        uint256 amount0,
        uint256 amount1
    );

    /**
     * @dev withdraw info
     * @param sender is msg.sender and the owner of the shares being burned
     * @param to is the receiver of the tokens earned via the burn
     * @param shares is the number of shares being burned
     * @param amount0 is the amount of t0 earned via the burn
     * @param amount1 is the amount of t1 earned via the burn
     */
    event Withdraw(
        address indexed sender,
        address indexed to,
        uint256 shares,
        uint256 amount0,
        uint256 amount1
    );

    /**
     * @dev info on protocol fees earned. Triggers whenever a withdraw or a tend happens.
     * @param amount0Earned is total t0 fees earned, including fees going to steer and strategist.
     * @param amount1Earned is total t1 fees earned, including fees going to steer and strategist.
     */
    event FeesEarned(uint256 amount0Earned, uint256 amount1Earned);

    //Useful if we upgrade to a contract which needs more storage slots
    uint256[100] gap;

    constructor() {}

    /**
     * @dev initializes vault
     * @param _orchestrator is address of orchestrator, which has permission to call tend.
     * @param _steer is the steer multisig address, responsible for some governance functions.
     * @param _internalGovernance is the internal governance contract address, responsible for more impactful governance functions.
     * @param _params is all other parameters this vault will use:
            param _token0 is address of token0
            param _token1 is address of token1
            param _poolFee is Uniswap pool fee, how much is charged for a swap
            param _maxTotalSupply is the maximum total supply of LP tokens before new deposits are rejected
            param _maxTickChange is max difference between TWAP tick and current tick, useful for protection against frontrunning. Cannot be negative.
            param _twapInterval is the number of seconds to get the time-weighted average over
     */
    function initialize(
        address _orchestrator,
        address _steer,
        address _internalGovernance,
        bytes memory _params
    ) public virtual initializer {
        (
            address _token0,
            address _token1,
            uint24 _poolFee,
            uint256 _maxTotalSupply,
            int24 _maxTickChange,
            uint32 _twapInterval
        ) = abi.decode(
            _params,
            (address, address, uint24, uint256, int24, uint32)
        );

        // Validate input parameters
        require(_maxTickChange >= 0);
        require(_token0 < _token1, "TOKEN");

        // context inits
        vaultRegistry = msg.sender;
        token0 = _token0;
        token1 = _token1;

        // Brackets to manage stack
        {
            // Get relevant pool from uniswap factory
            address _pool = FACTORY.getPool(_token0, _token1, _poolFee);

            // Revert if pool doesn't exist yet
            require(_pool != address(0));

            // Set pool
            pool = IUniswapV3Pool(_pool);
        }

        // Init the LP token ERC20 using totalVaultCount from the vaultRegistry
        // Naming system: STEER_VAULT_$TotalVaultCount, i.e. STEER_VAULT_20
        string memory vaultCount = StringsUpgradeable.toString(
            IBareVaultRegistry(msg.sender).totalVaultCount() + 1
        );
        __ERC20_init(
            string(abi.encodePacked("STEER_VAULT_", vaultCount)),
            string(abi.encodePacked("str_", vaultCount))
        );

        // Init everything else
        __Pausable_init();
        __AccessControl_init();

        _setupRole(STEER_ROLE, _steer);
        _setupRole(ORCHESTRATOR_ROLE, _orchestrator);
        _setupRole(GOVERNANCE_ROLE, _internalGovernance);

        // Set max supply
        maxTotalSupply = _maxTotalSupply;

        // Set security params
        maxTickChange = _maxTickChange;
        twapInterval = _twapInterval;
    }

    /**
     * @notice Deposits tokens in proportion to the vault's current holdings.
     * @dev These tokens sit in the vault and are not used for liquidity on
     * Uniswap until the next rebalance.
     * @param amount0Desired Max amount of token0 to deposit
     * @param amount1Desired Max amount of token1 to deposit
     * @param amount0Min Revert if resulting amount0Used is less than this
     * @param amount1Min Revert if resulting amount1Used is less than this
     * @param to Recipient of shares
     * @return shares Number of shares minted
     * @return amount0Used Amount of token0 deposited
     * @return amount1Used Amount of token1 deposited
 
     * @notice function requirements:  
        Either amount0Desired or amount1Desired must be > 0 or it will revert with 'CROSS'
     */
    function deposit(
        uint256 amount0Desired,
        uint256 amount1Desired,
        uint256 amount0Min,
        uint256 amount1Min,
        address to
    )
        public
        virtual
        whenNotPaused
        returns (
            uint256 shares,
            uint256 amount0Used,
            uint256 amount1Used
        )
    {
        // Update pool fees earned
        poke();

        // Calculate amounts proportional to vault's holdings
        (shares, amount0Used, amount1Used) = _calcSharesAndAmounts(
            amount0Desired,
            amount1Desired
        );

        // Make sure deposit meets slippage requirements. If amount0Used < amount0Min or amount1Used < amount1Min, there has been too much slippage.
        require(shares != 0, "shares");
        require(amount0Used >= amount0Min, "amt0");
        require(amount1Used >= amount1Min, "amt1");

        // Pull in tokens from sender
        if (amount0Used != 0) {
            IERC20(token0).safeTransferFrom(
                msg.sender,
                address(this),
                amount0Used
            );
        }
        if (amount1Used != 0) {
            IERC20(token1).safeTransferFrom(
                msg.sender,
                address(this),
                amount1Used
            );
        }

        // Mint shares to recipient
        _mint(to, shares);
        require(totalSupply() <= maxTotalSupply, "TOT"); // totalSupply after deposit must not exceed max total supply
        emit Deposit(msg.sender, to, shares, amount0Used, amount1Used);
    }

    function poke() public virtual;

    /** 
     * @dev Wrapper around `IUniswapV3Pool.positions()`.
     * @param tickLower lower bound of position whose info is requested
     * @param tickUpper upper bound of position
     * @return 
        uint128 liquidity - the amount of liquidity owned by this position
        uint256 feeGrowthInside0LastX128 - fee growth per unit of liquidity as of the last update to liquidity or fees owed
        uint256 feeGrowthInside1LastX128 - fee growth per unit of liquidity as of the last update to liquidity or fees owed
        uint128 tokensOwed0 - token0 owed to the contract
        uint128 tokensOwed1 - token1 owed to the contract
    */
    function _position(int24 tickLower, int24 tickUpper)
        internal
        view
        returns (
            uint128,
            uint256,
            uint256,
            uint128,
            uint128
        )
    {
        bytes32 positionKey = PositionKey.compute(
            address(this),
            tickLower,
            tickUpper
        );
        return pool.positions(positionKey);
    }

    /// @dev Calculates the largest possible `amount0` and `amount1` such that
    /// they're in the same proportion as total amounts, but not greater than
    /// `amount0Desired` and `amount1Desired` respectively.
    function _calcSharesAndAmounts(
        uint256 amount0Desired,
        uint256 amount1Desired
    )
        internal
        view
        returns (
            uint256 shares,
            uint256 amount0Used,
            uint256 amount1Used
        )
    {
        uint256 _totalSupply = totalSupply();
        (uint256 total0, uint256 total1) = getTotalAmounts();

        // If total supply > 0, vault can't be empty.
        assert(_totalSupply == 0 || total0 > 0 || total1 > 0);

        if (_totalSupply == 0) {
            // For first deposit, just use the amounts desired
            amount0Used = amount0Desired;
            amount1Used = amount1Desired;
            shares = Math.max(amount0Used, amount1Used);
        } else if (total0 == 0) {
            amount1Used = amount1Desired;
            shares = FullMath.mulDiv(amount1Used, _totalSupply, total1);
        } else if (total1 == 0) {
            amount0Used = amount0Desired;
            shares = FullMath.mulDiv(amount0Used, _totalSupply, total0);
        } else {
            uint256 cross = Math.min(
                amount0Desired.mul(total1),
                amount1Desired.mul(total0)
            );

            // If cross is zero, this means that the inputted ratio is totally wrong and must be adjusted to better match the vault's held ratio.
            // This pretty much only happens if all of the vault's holdings are in one token, and the user wants to exclusively deposit the other token, or if
            // someone is attempting to abuse a rounding exploit.
            require(cross != 0, "cross");

            // Round up amounts
            // cross - 1 can be unchecked since above we require cross != 0
            amount0Used = ((cross - 1) / total1) + 1;
            amount1Used = ((cross - 1) / total0) + 1;
            shares = FullMath.mulDiv(cross, _totalSupply, total0) / total1;
        }
    }

    function getTotalAmounts() public view virtual returns (uint256, uint256);

    /**
     * @notice Withdraws tokens in proportion to the vault's holdings.
     * @param shares Shares burned by sender
     * @param amount0Min Revert if resulting `amount0` is smaller than this
     * @param amount1Min Revert if resulting `amount1` is smaller than this
     * @param to Recipient of tokens
     * @return amount0 Amount of token0 sent to recipient
     * @return amount1 Amount of token1 sent to recipient
     */
    function withdraw(
        uint256 shares,
        uint256 amount0Min,
        uint256 amount1Min,
        address to
    ) external virtual returns (uint256 amount0, uint256 amount1) {
        require(shares != 0, "shares"); //Shares to withdraw must be greater than zero
        uint256 _totalSupply = totalSupply();

        // Burn LPTs
        _burn(msg.sender, shares);

        // Calculate token amounts proportional to unused balances
        // LP value = (Value deposited in Uniswap + value held in vault undeposited) / total LP tokens. Here we calculate value held in vault undeposited.
        // No div(0) safemath here because totalSupply != 0; since shares != 0 and totalSupply >= shares
        amount0 = FullMath.mulDiv(getBalance0(), shares, _totalSupply);
        amount1 = FullMath.mulDiv(getBalance1(), shares, _totalSupply);

        // Withdraw proportion of liquidity from Uniswap pool
        (uint256 t0FromPool, uint256 t1FromPool) = _burnAndCollect(
            shares,
            _totalSupply
        );

        // In addition to their share of vault-held tokens, withdraw their share of uniswap-held tokens.
        amount0 = amount0.add(t0FromPool);
        amount1 = amount1.add(t1FromPool);

        require(amount0 >= amount0Min, "amt0");
        require(amount1 >= amount1Min, "amt1");

        // Push tokens to recipient
        if (amount0 != 0) IERC20(token0).safeTransfer(to, amount0);
        if (amount1 != 0) IERC20(token1).safeTransfer(to, amount1);

        emit Withdraw(msg.sender, to, shares, amount0, amount1);
    }

    function _burnAndCollect(uint256 shares, uint256 totalShares)
        internal
        virtual
        returns (
            uint256 t0,     // token0 earned from burned liquidity + fees. Only includes burned + fees corresponding to LP shares being withdrawn (100% if tend)
            uint256 t1      // token1 earned from burned liquidity + fees
        );

    /**
     * @param currentTick is current pool tick
     * @dev revert if volatility is above acceptable levels (mainly used to prevent flashloan attacks)
     */
    function _checkVolatility(int24 currentTick) internal view {
        // SLOADS for efficiency
        uint32 _twapInterval = twapInterval;
        int24 _maxTickChange = maxTickChange;

        // Get TWAP tick
        uint32[] memory secondsAgos = new uint32[](2);
        secondsAgos[0] = _twapInterval; //From 45 seconds ago
        secondsAgos[1] = 0;

        // tickCumulatives is basically where the tick was as of twapInterval seconds ago
        (int56[] memory tickCumulatives, ) = pool.observe(secondsAgos);
        int24 twapTick = int24(
            // tickCumulatives[1] will always be greater than [0] so no need to check for underflow or division overflow here.
            (tickCumulatives[1] - tickCumulatives[0]) / _twapInterval
        );

        // Make sure currentTick is not more than maxTickChange ticks away from twapTick
        // No SafeMath here--even if a compromised governance contract set _maxTickChange to a very high value, it would only wrap around and cause this check to fail.
        require(
            currentTick <= twapTick + _maxTickChange &&
                currentTick >= twapTick - _maxTickChange,
            "VOL"
        );
    }

    /**
     * @notice Balance of token0 in vault not used in any position.
     */
    function getBalance0() internal view returns (uint256) {
        return
            IERC20(token0)
                .balanceOf(address(this))
                .sub(accruedSteerFees0 + accruedStrategistFees0); // If accrued fees > the token balance, the contract is already very broken elsewhere
    }

    /**
     * @notice Balance of token1 in vault not used in any position.
     */
    function getBalance1() internal view returns (uint256) {
        return
            IERC20(token1).balanceOf(address(this))
            .sub(accruedSteerFees1 + accruedStrategistFees1); // If accrued fees > the token balance, the contract is already very broken elsewhere
    }

    /**
     * @notice Amounts of token0 and token1 held in this position of the vault. Includes
     * owed fees. Also includes fees paid to the protocol, so not everything returned here is owned by LPs.
     * Doesn't include fees accrued since last poke (so includes all fees if poke was called as part of this transaction)
     */
    function getPositionAmounts(
        uint160 sqrtPriceX96,
        int24 tickLower,
        int24 tickUpper
    )
        internal
        view
        returns (
            uint256 amount0,
            uint256 amount1,
            uint256 fees0,
            uint256 fees1
        )
    {
        (
            uint128 liquidity,
            ,
            ,
            uint128 tokensOwed0,
            uint128 tokensOwed1
        ) = _position(tickLower, tickUpper);

        (amount0, amount1) = LiquidityAmounts.getAmountsForLiquidity(
            sqrtPriceX96,
            TickMath.getSqrtRatioAtTick(tickLower),
            TickMath.getSqrtRatioAtTick(tickUpper),
            liquidity
        );

        return (amount0, amount1, tokensOwed0, tokensOwed1);
    }

    /// @dev Callback for Uniswap V3 pool.
    function uniswapV3MintCallback(
        uint256 amount0,
        uint256 amount1,
        bytes calldata data
    ) external override {
        require(msg.sender == address(pool));
        if (amount0 != 0) IERC20(token0).safeTransfer(msg.sender, amount0);
        if (amount1 != 0) IERC20(token1).safeTransfer(msg.sender, amount1);
    }

    /// @dev Callback for Uniswap V3 pool.
    function uniswapV3SwapCallback(
        int256 amount0Wanted,
        int256 amount1Wanted,
        bytes calldata data
    ) external override {
        require(msg.sender == address(pool));
        if (amount0Wanted > 0)
            IERC20(token0).safeTransfer(msg.sender, uint256(amount0Wanted));
        if (amount1Wanted > 0)
            IERC20(token1).safeTransfer(msg.sender, uint256(amount1Wanted));
    }

    /**
     * @notice Used to collect accumulated protocol fees.
     */
    function steerCollectFees(
        uint256 amount0,
        uint256 amount1,
        address to
    ) external onlyRole(STEER_ROLE) whenNotPaused {
        if (amount0 != 0) {
            accruedSteerFees0 = accruedSteerFees0.sub(amount0);
            IERC20(token0).safeTransfer(to, amount0);
        }

        if (amount1 != 0) {
            accruedSteerFees1 = accruedSteerFees1.sub(amount1);
            IERC20(token1).safeTransfer(to, amount1);
        }
    }

    /**
     * @notice Used to collect accumulated protocol fees.
     */
    function strategistCollectFees(
        uint256 amount0,
        uint256 amount1,
        address to
    ) external whenNotPaused {
        // Make sure msg.sender is NFT holder of this vault
        require(
            msg.sender ==
                IBareVaultRegistry(vaultRegistry).getStrategyCreatorForVault(
                    address(this)
                )
        );

        if (amount0 != 0) {
            accruedStrategistFees0 = accruedStrategistFees0.sub(amount0);
            IERC20(token0).safeTransfer(to, amount0);
        }

        if (amount1 != 0) {
            accruedStrategistFees1 = accruedStrategistFees1.sub(amount1);
            IERC20(token1).safeTransfer(to, amount1);
        }
    }

    /**
     * @dev Used to change deposit cap for a guarded launch or to ensure
     * vault doesn't grow too large relative to the pool. Cap is on total
     * supply rather than amounts of token0 and token1 as those amounts
     * fluctuate naturally over time.
     * @param _maxTotalSupply is the new max total supply of LP tokens.
     */
    function setMaxTotalSupply(uint256 _maxTotalSupply)
        external
        whenNotPaused
        onlyRole(GOVERNANCE_ROLE)
    {
        maxTotalSupply = _maxTotalSupply;
    }

    /**
     * @notice Removes liquidity in case of emergency.
     */
    function emergencyBurn(
        int24 tickLower,
        int24 tickUpper,
        uint128 liquidity
    ) external onlyRole(STEER_ROLE) returns (uint256 amount0, uint256 amount1) {
        (amount0, amount1) = pool.burn(tickLower, tickUpper, liquidity);
        pool.collect(
            address(this),
            tickLower,
            tickUpper,
            type(uint128).max,
            type(uint128).max
        );
    }

    /**
     * @notice Used by periphery to verify vault is depositable.
     */
    function supportsInterface(bytes4 interfaceId) external view returns (bool) {
        return IBareVaultRegistry(vaultRegistry).doISupportInterface(interfaceId);
    }

    // Pause and unpause
    function pause() external onlyRole(STEER_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(STEER_ROLE) {
        _unpause();
    }
}

contracts/interfaces/IBareVaultRegistry..content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.7.6;

/**
 * Used only as a 0.7.6 registry for the UniLiquidityManager.
 */
interface IBareVaultRegistry   {
    /// @notice Retrieves the creator of a given vault
    /// @param _vault The address of the vault
    /// @return The address of the creator
    function getStrategyCreatorForVault(address _vault)
        external
        view
        returns (address);

    //Total vault count
    function totalVaultCount() external view returns(uint256);

    function whitelistRegistry() external view returns(address);

    function doISupportInterface(bytes4 interfaceId) external view returns(bool);
}

contracts/interfaces/IBaseDeposit.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.7.6;

/// @dev Interface for supporting depositing 2 assets via periphery
interface IBaseDeposit {

        ///
    /// @dev Deposits tokens in proportion to the vault's current holdings.
    /// @dev These tokens sit in the vault and are not used for liquidity on
    /// Uniswap until the next rebalance.
    /// @param amount0Desired Max amount of token0 to deposit
    /// @param amount1Desired Max amount of token1 to deposit
    /// @param amount0Min Revert if resulting `amount0` is less than this
    /// @param amount1Min Revert if resulting `amount1` is less than this
    /// @param to Recipient of shares
    /// @return shares Number of shares minted
    /// @return amount0 Amount of token0 deposited
    /// @return amount1 Amount of token1 deposited
    ///
    function deposit(
        uint256 amount0Desired,
        uint256 amount1Desired,
        uint256 amount0Min,
        uint256 amount1Min,
        address to
    )
        external
        returns (
            uint256 shares,
            uint256 amount0,
            uint256 amount1
        );
}

