contracts/vault-types/UniLiquidityManage.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.7.6;
pragma abicoder v2;

import "./BaseLiquidityManager.sol";

contract SinglePositionLiquidityManager is BaseLiquidityManager    {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    
    /**
     * @dev the vault's position's data. This represents the active position of the vault inside uniswap.
     * lowerTick is the lower bound of the position at that index.
     * upperTick is the upper bound of the position at that index.
     */
    int24 public lowerTick;
    int24 public upperTick;

    /**
     * @dev burns vault position, updating fees owed to that position.
     * @notice call this before calling getTotalAmounts if total amounts must include fees. There's a function in the periphery to do so through a static call.
     */
    function poke() 
        public
        override
    {
        int24 _lowerTick = lowerTick;
        int24 _upperTick = upperTick;
        (uint128 positionLiquidity, , , , ) = _position(
            _lowerTick,
            _upperTick
        );

        // Update fees owed.
        if (positionLiquidity > 0)   {
            pool.burn(_lowerTick, _upperTick, 0);
        }
    }

    /**
     * @notice Internal function to pull funds from pool, update position if necessary, then deposit funds into pool.
     * @notice reverts if the vault does not own any liquidity or tokens.
     * @param totalWeight is the share of liquidity we want deposited, multiplied by 10,000. A value of 10,000 means we want to deposit all tokens into uniswap.
        A value of 0 means we just want all the liquidity out. Note that values above 10,000 are not explicitly prohibited but will generally cause 
        the tend to fail and revert.

     * New position details:
     * @param newLowerTick: the lower tick of the new position to be created
     * @param newUpperTick: the upper tick of the new position to be created.
     * @dev note that if both are set to the same value, the vault's position will not be updated. Gas will be cheapest if both are set to zero in this case.

     * time-sensitive parameters:
     * @param swapAmount ithe amount to be swapped from one token to another this tend. zeroForOne if positive, oneForZero if negative.
     * @param sqrtPriceLimitX96: the slippage limit of the swap. Protections elsewhere prevent extreme slippage if the keeper calling this 
            function is malicious, but this is the first line of defense against MEV attacks.

     * @notice tick requirements:
     * lowerTick must be lower than upperTick
     * lowerTick must be greater than or equal to the tick min (-887272)
     * upperTick must be less than or equal to the tick max (887272)
     * lowerTick and upperTick must be divisible by the pool tickSpacing--
        A 0.05% fee pool has tick spacing of 10, 0.3% has tick spacing 60. and 1% has tick spacing 200.
     */
    function tend(
        uint256 totalWeight,
        int24 newLowerTick,
        int24 newUpperTick,
        int256 swapAmount,
        uint160 sqrtPriceLimitX96
    ) external onlyRole(ORCHESTRATOR_ROLE) whenNotPaused {
        // Get current pool state
        (uint160 sqrtPriceX96, int24 currentTick, , , , , ) = pool.slot0();

        // currentTick must be close enough to TWAP tick to avoid MEV exploit--this is essentially a way to prevent a flashloan attack even if sqrtPriceLimit is set incorrectly.
        _checkVolatility(currentTick); 

        // Withdraw liquidity from Uniswap pool by passing in 1 and 1 (indicating we're withdrawing 100% of liquidity)
        _burnAndCollect(
            1,
            1
        );

        // Update positions if desired. If both are set to zero (or just the same amount) then we won't update the positions, instead leaving them as they were.
        if (newLowerTick != newUpperTick)   {
            lowerTick = newLowerTick;
            upperTick = newUpperTick;
        }

        // Perform a swap if desired.
        if (swapAmount > 0) {
            bool zeroForOne = swapAmount > 0;
            pool.swap(
                address(this),
                zeroForOne,
                zeroForOne ? swapAmount : -swapAmount,
                sqrtPriceLimitX96,
                ""
            );

            // Update sqrtPriceX96; it will have moved due to the swap
            (sqrtPriceX96, , , , , , ) = pool.slot0();
        }

        uint256 balance0 = getBalance0();
        uint256 balance1 = getBalance1();
        emit Snapshot(
            sqrtPriceX96,
            balance0,
            balance1,
            totalSupply()
        );

        // Create new positions in Uniswap
        if (totalWeight > 0)   {
            setBins(
                sqrtPriceX96, 
                FullMath.mulDiv(balance0, totalWeight, 1e4), // balance0 adjusted by totalWeight
                FullMath.mulDiv(balance1, totalWeight, 1e4), // balance1 adjusted by totalWeight
                swapAmount
            );
        }
    }

        /**
     * @dev given desired positions, desired relative weights, and a current token amount, 
        this function deposits as much liquidity as possible into each position while respecting relative weights.
     * @param sqrtPriceX96 is the current sqrtPriceX96 of the pool
     * @param balance0 is the vault's current balance of token0 ready to be deposited (excluding steer and strategist fees)
     * @param balance1 is the vault's current balance of token1 ready to be deposited (excluding steer and strategist fees)
     * @param swapAmount is the amount to be swapped from one token to another this tend. zeroForOne if positive, oneForZero if negative.
        Here it is mainly used to determine which direction the swap was, so that we can check whether the swap was too large.
     */
    function setBins (
        uint160 sqrtPriceX96,
        uint256 balance0,
        uint256 balance1,
        int256 swapAmount
    ) internal {
        int24 _lowerTick = lowerTick;
        int24 _upperTick = upperTick;

        uint128 liquidity = LiquidityAmounts.getLiquidityForAmounts(
            sqrtPriceX96,
            TickMath.getSqrtRatioAtTick(_lowerTick),
            TickMath.getSqrtRatioAtTick(_upperTick),
            balance0,
            balance1
        );

        // Create the position inside the pool.
        if (liquidity > 0) {
            pool.mint(address(this), _lowerTick, _upperTick, liquidity, "");
        }

        /*
            Check post-mint balances. We need to check that less than 5% of the TO token (i.e. the token we swapped into) remains post-mint.
            Having the right liquidity ratio is extremely valuable, but the main thing we're protecting against here is dynamic data that swaps more than it should. 

            As an example, assume a malicious keeper has flashloaned the uniswap V3 pool into a very bad exchange rate before handling the swap.
            If exchange rate is extremely high token1PerToken0, exploiter will want to swap from token1 to token0 (going the other way just helps the liquidity manager)
            but the positions will be entirely in token1. The check below ensures that no more than 5% of the total contract token0 remains undeposited, a reliable indicator
            that the correct amount of token1 was swapped.

            This combined with the TWAP check makes flashloan exploits extremely difficult for a single keeper.
        */
        // If swapAmount > 0, that means zeroForOne. Otherwise, oneForZero. No overflow checks here because some kind of overflow exploit is both implausible and would just
        // cause a revert.
        if (swapAmount > 0)   {
            //Require that at least 95% of t1 has been deposited (ensuring that swap amount wasn't too great)
            require(getBalance1() < (balance1 * 95) / 100, 'swap');
        } else if (swapAmount < 0)   {
            //Require that at least 95% of t0 has been deposited (ensuring that swap amount wasn't too great)
            require(getBalance0() < (balance0 * 95) / 100, 'swap');
        }
    }

    /// @dev Withdraws liquidity from position, allocating fees correctly in the process.
    /// @param shares LP shares being withdrawn
    /// @param totalShares total # of LP tokens in the vault
    function _burnAndCollect(
        uint256 shares,
        uint256 totalShares
    )
        internal
        override
        returns (
            uint256 t0,    //token0 earned from burned liquidity + fees. Only includes burned + fees corresponding to LP shares being withdrawn (100% if tend)
            uint256 t1     //token1 earned from burned liquidity + fees
        )
    {
        // Cache lowerTick and upperTick to minimize SLOADs
        int24 _lowerTick = lowerTick;
        int24 _upperTick = upperTick;

        // Burn() and then withdraw correct amount of liquidity.

        // Get position liquidity. If we don't want all of it, here is where we specify how much we want (liquidity * fraction of pool which is being withdrawn)
        (uint128 totalPositionLiquidity, , , , ) = _position(
            _lowerTick,
            _upperTick
        );

        // Shares is always >= totalShares so no risk of uint128 overflow here.
        uint128 liquidityToBurn = uint128(FullMath.mulDiv(
            totalPositionLiquidity,
            shares,
            totalShares
        ));

        // amountsOwed are always pulled with liquidity in this contract, so if position liquidity is 0, no need to withdraw anything.
        if (liquidityToBurn == 0)   {
            return (0, 0);
        }

        // If there is liquidity to withdraw, proceed to withdraw it and also collect fees.

        // Amount burned in each position. Corresponds to amount of liquidity withdrawn (i.e. doesn't include fees).
        (t0, t1) = pool.burn(_lowerTick, _upperTick, liquidityToBurn);

        // Collect all owed tokens including earned fees
        (uint256 collect0, uint256 collect1) = pool.collect(
            address(this),
            _lowerTick,
            _upperTick,
            type(uint128).max,
            type(uint128).max
        );

        // Fees earned by liquidity inside uniswap = collected - burned. First we allocate some to steer and some to strategist. The remainder is the fees earned by LPs.
        // So after that we add remainder * shares / totalShares, and that gives us t0 and t1 allocated to whatever's being withdrawn.

        // Since burned will never be greater than collected, no need to check for underflow here.
        uint256 fees0 = collect0 - t0;
        uint256 fees1 = collect1 - t1;

        // Emit fee info
        emit FeesEarned(
            fees0,
            fees1
        );

        // Update accrued protocol fees
        if (fees0 > 0)   {
            uint256 totalCut0 = FullMath.mulDiv(
                fees0,
                TOTAL_FEE,
                FEE_DIVISOR
            );

            // Subtract fees going to strategist/steer from fees going to vault
            fees0 -= totalCut0;

            // Allocate fee amounts to strategist/steer correctly
            uint256 feesToSteer0 = FullMath.mulDiv(
                totalCut0,
                STEER_FRACTION_OF_FEE,
                FEE_DIVISOR
            );

            // Since steer fees = (totalCut * steerFraction) / 1e4, and steerFraction cannot be greater than 1e4, no need to check for underflow here.
            uint256 feesToStrategist0 = totalCut0 - feesToSteer0;

            // Increment fees
            accruedSteerFees0 = accruedSteerFees0.add(feesToSteer0);
            accruedStrategistFees0 = accruedStrategistFees0.add(
                feesToStrategist0
            );
        }

        if (fees1 > 0)   {
            uint256 totalCut1 = FullMath.mulDiv(
                fees1,
                TOTAL_FEE,
                FEE_DIVISOR
            );
            fees1 -= totalCut1;

            uint256 feesToSteer1 = FullMath.mulDiv(
                totalCut1,
                STEER_FRACTION_OF_FEE,
                FEE_DIVISOR
            );
            uint256 feesToStrategist1 = totalCut1 - feesToSteer1;
            
            accruedSteerFees1 = accruedSteerFees1.add(feesToSteer1);
            accruedStrategistFees1 = accruedStrategistFees1.add(
                feesToStrategist1
            );
        }
        
        // Add fees earned by burned position to burned amount
        t0 = t0.add(FullMath.mulDiv(
            fees0,
            shares,
            totalShares
        ));

        t1 = t1.add(FullMath.mulDiv(
            fees1,
            shares,
            totalShares
        ));
    }

    /**
     * @notice Calculates the vault's total holdings of token0 and token1 - in
     *      other words, how much of each token the vault would hold if it withdrew
     *      all its liquidity from Uniswap.
     * @notice this function DOES NOT include fees earned since the last burn. 
        To include fees, first poke() and then call getTotalAmounts. There's a function inside the periphery to do so.
     */
    function getTotalAmounts() public override view returns (uint256 total0, uint256 total1)   {
        // Start with tokens currently held inside the vault
        total0 = getBalance0();
        total1 = getBalance1();

        // These include fees to steer and strategist, which we remove before adding them to total0 and total1.
        uint256 totalT0Fees;
        uint256 totalT1Fees;

        (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();

        // Get calculated amounts of tokens contained within position
        (uint256 amt0, uint256 amt1, uint256 fees0, uint256 fees1) = getPositionAmounts(
            sqrtPriceX96,
            lowerTick,
            upperTick
        );

        // Increment balances
        total0 = total0.add(amt0);
        total1 = total1.add(amt1);

        // Subtract protocol fees from position fee earned, then add the (now LP-owned) remaining tokens to total balances
        total0 = total0.add(FullMath.mulDiv(
            fees0,
            ONE_MINUS_FEE,
            FEE_DIVISOR
        ));
        total1 = total1.add(FullMath.mulDiv(
            fees1,
            ONE_MINUS_FEE,
            FEE_DIVISOR
        ));
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.7.6;
pragma abicoder v2;

import {BaseLiquidityManager} from "./BaseLiquidityManager.sol";
import {MultiPositionLiquidityManager} from "./MultiPositionLiquidityManager.sol";
import {IBareWhitelistRegistry} from "contracts/interfaces/IBareWhitelistRegistry.sol";
import {IBareVaultRegistry} from "contracts/interfaces/IBareVaultRegistry.sol";

// MultiPositionLiquidityManager with whitelist-gated deposits
contract WhitelistedMultiLiquidityManager is MultiPositionLiquidityManager {
    /// @dev the contract this vault will check for whitelisting permissions
    address public whitelistManager;

    /**
     * @dev initializes vault
     * @param _orchestrator is address of orchestrator, which has permission to call tend.
     * @param _steer is the steer multisig address, responsible for some governance functions.
     * @param _internalGovernance is the internal governance contract address, responsible for more impactful governance functions.
     * @param _params is all other parameters this vault will use:
        _params.whitelistManager is the address (usually an EOA) in charge of adding/removing accounts from the whitelist
        _params.setupParams can be found in the MultiLiquidityManager
     */
    function initialize(
        address _orchestrator,
        address _steer,
        address _internalGovernance,
        bytes memory _params
    ) public virtual override(BaseLiquidityManager) {
        // not initializer because super is initializer
        // Get whitelist data
        (address _whitelistManager, bytes memory setupParams) = abi.decode(
            _params,
            (address, bytes)
        );

        // Set up whitelist
        address _whitelistRegistry = IBareVaultRegistry(msg.sender)
            .whitelistRegistry();
        IBareWhitelistRegistry(_whitelistRegistry).registerWhitelistManager(
            _whitelistManager
        );
        whitelistManager = _whitelistRegistry;

        // Set up vault
        super.initialize(
            _orchestrator,
            _steer,
            _internalGovernance,
            setupParams
        );
    }

    /**
     * @dev Checks that user is authorized to deposit, then deposits
     * @dev check BaseLiquidityManager for more info on the params
     */
    function deposit(
        uint256 amount0Desired,
        uint256 amount1Desired,
        uint256 amount0Min,
        uint256 amount1Min,
        address to
    )
        public
        virtual
        override
        returns (
            uint256 shares,
            uint256 amount0Used,
            uint256 amount1Used
        )
    {
        // Check that user is authorized to deposit
        require(
            IBareWhitelistRegistry(whitelistManager).permissions(
                address(this),
                to
            ) == 1,
            "whitelist"
        );

        // Deposit
        return
            super.deposit(
                amount0Desired,
                amount1Desired,
                amount0Min,
                amount1Min,
                to
            );
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.7.6;
pragma abicoder v2;

import "./BaseLiquidityManager.sol";

contract MultiPositionLiquidityManager is BaseLiquidityManager    {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    /**
     * @dev the vault's position data. At any given moment this represents all active positions inside the pool.
     * Each lowerTick is the lower bound of the position at that index.
     * Each upperTick is the upper bound of the position at that index.
     * Each relativeWeight is the relative weight of the position at that index, relative to the other positions.
     * So for example if LiquidityPositions is 
        {
            lowerTicks: [0, 20, 40],
            upperTicks: [10, 30, 50],
            relativeWeights: [1, 2, 3]
        }
        then that means the vault has 3 positions: 
            1. 0-10 with relative weight 1
            2. 20-30 with relative weight 2
            3. 40-50 with relative weight 3
     */
    struct LiquidityPositions {
        int24[] lowerTick;
        int24[] upperTick;
        uint16[] relativeWeight;
    }

    LiquidityPositions internal positions;
    
    /**
     * @dev get current positions held by the vault
     */
    function getPositions() external view returns(int24[] memory, int24[] memory, uint16[] memory)   {
        LiquidityPositions memory _positions = positions;
        return (_positions.lowerTick, _positions.upperTick, _positions.relativeWeight);
    }

    /**
     * @dev burns each vault position which contains liquidity, updating fees owed to that position.
     * @notice call this before calling getTotalAmounts if total amounts must include fees. There's a function in the periphery to do so through a static call.
     */
    function poke() 
        public
        override
    {
        LiquidityPositions memory tempPos = positions;
        uint256 positionCount = tempPos.lowerTick.length;
        for (uint256 i; i != positionCount; ++i) {
            //Get position liquidity so that we can ignore this position if it has 0 liquidity.
            (uint128 positionLiquidity, , , , ) = _position(
                tempPos.lowerTick[i],
                tempPos.upperTick[i]
            );

            // If position has liquidity, update fees owed.
            if (positionLiquidity != 0) {
                pool.burn(tempPos.lowerTick[i], tempPos.upperTick[i], 0);
            }
        }
    }

    /**
     * @notice Internal function to pull funds from pool, update positions if necessary, then deposit funds into pool.
     * @notice reverts if the vault does not own any liquidity or tokens.
     * @param totalWeight is the share of liquidity we want deposited, multiplied by 10,000. A value of 10,000 means we want to deposit all tokens into uniswap.
        A value of 0 means we just want all the liquidity out. Note that values above 10,000 are not explicitly prohibited but will generally cause 
        the tend to fail.
     * @param newPositions is the info of each new position to be set.
        newPositions.lowerTick[] is an array, in order, of the lower ticks of each position
        newPositions.upperTick[] is an array, in order, of the upper ticks of each position
        newPositions.relativeWeight[] is an array, in order, of the relative weights of each position

        So if for example newPositions is called with lowerTick = [-120, 0], upperTick = [-60, 60], and relativeWeight = [1, 5], then 
        the positions would be -120 to -60 with a weight of 1, and 0 to 60 with a weight of 5. The weight differences roughly correspond to what 
        proportion of the liquidity is added to each position.

     * @param timeSensitiveData is encoded info of the swapAmount and sqrtPriceLimitX96. It must be encoded as bytes so that it the data is placed after
        the newPositions, which is also a dynamic data type.
            timeSensitiveData.swapAmount: the amount to be swapped from one token to another this tend. zeroForOne if positive, oneForZero if negative.
            timeSensitiveData.sqrtPriceLimitX96: the slippage limit of the swap. Protections elsewhere prevent extreme slippage if the keeper calling this 
            function is malicious, but this is the first line of defense against MEV attacks.

     * @notice newPositions requirements:
     * Each lowerTick must be lower than its corresponding upperTick
     * Each lowerTick must be greater than or equal to the tick min (-887272)
     * Each upperTick must be less than or equal to the tick max (887272)
     * All lowerTicks and upperTicks must be divisible by the pool tickSpacing--
        A 0.05% fee pool has tick spacing of 10, 0.3% has tick spacing 60. and 1% has tick spacing 200.)
     */
    function tend(
        uint256 totalWeight,
        LiquidityPositions memory newPositions,
        bytes calldata timeSensitiveData
    ) external onlyRole(ORCHESTRATOR_ROLE) whenNotPaused {
        (int256 swapAmount, uint160 sqrtPriceLimitX96) = abi.decode(timeSensitiveData, (int256, uint160));

        // Get current pool state
        (uint160 sqrtPriceX96, int24 currentTick, , , , , ) = pool.slot0();

        // currentTick must be close enough to TWAP tick to avoid MEV exploit--this is essentially a way to prevent a flashloan attack even if sqrtPriceLimit is set incorrectly.
        _checkVolatility(currentTick); 

        // Withdraw liquidity from Uniswap pool by passing in 1 and 1 (indicating we're withdrawing 100% of liquidity)
        _burnAndCollect(
            1,
            1
        );

        // Update positions if desired. If newPositions is empty, we'll just continue with the old positions instead.
        if (newPositions.lowerTick.length > 0)   {
            positions = newPositions;
        }

        //Perform a swap if desired.
        if (swapAmount != 0) {
            bool zeroForOne = swapAmount > 0;
            pool.swap(
                address(this),
                zeroForOne,
                zeroForOne ? swapAmount : -swapAmount,
                sqrtPriceLimitX96,
                ""
            );

            // Update sqrtPriceX96; it will have moved due to the swap
            (sqrtPriceX96, , , , , , ) = pool.slot0();
        }

        uint256 balance0 = getBalance0();
        uint256 balance1 = getBalance1();
        emit Snapshot(
            sqrtPriceX96,
            balance0,
            balance1,
            totalSupply()
        );

        // Create new positions in Uniswap
        if (totalWeight != 0)   {
            setBins(
                sqrtPriceX96, 
                FullMath.mulDiv(balance0, totalWeight, 1e4), // balance0 adjusted by totalWeight
                FullMath.mulDiv(balance1, totalWeight, 1e4), // balance1 adjusted by totalWeight
                swapAmount
            );
        }
    }

    /**
     * @dev given desired positions, desired relative weights, and a current token amount, 
        this function deposits as much liquidity as possible into each position while respecting relative weights.
     * @param sqrtPriceX96 is the current sqrtPriceX96 of the pool
     * @param balance0 is the vault's current balance of token0 ready to be deposited (excluding steer and strategist fees)
     * @param balance1 is the vault's current balance of token1 ready to be deposited (excluding steer and strategist fees)
     * @param swapAmount is the amount to be swapped from one token to another this tend. zeroForOne if positive, oneForZero if negative.
        Here it is mainly used to determine which direction the swap was, so that we can check whether the swap was too large.
     */
    function setBins (
        uint160 sqrtPriceX96,
        uint256 balance0,
        uint256 balance1,
        int256 swapAmount
    ) internal {
        LiquidityPositions memory tempPos = positions;

        // Get relative amounts of t0 and t1 for each position. 
        (
            uint256[] memory positionT0Requested,
            uint256[] memory positionT1Requested,
            uint256 totalT0Requested,
            uint256 totalT1Requested
        ) = _computePositionAmounts(sqrtPriceX96);

        // Now add liquidity to those bins based on their weights vs total token weights.
        /*
            If relativeWeights have a bad input (such as a weight of 0, or a very high weight, in one of the positions) the below code will revert in some cases,
            proceed in others. The result will not be correct but all that a bad input can do is cause a revert or cause less than 100% of liquidity to be deployed.
         */
        uint256 positionCount = tempPos.lowerTick.length;

        for (uint256 i; i != positionCount; ++i) {
            // Liquidity to deposit for this position is calculated using _liquidityForAmounts and the calculated tokens to deposit for the position.

            // Set token amounts for this position
            uint256 positionT0Amount;
            uint256 positionT1Amount;

            if (totalT0Requested > 0) {
                positionT0Amount =
                    FullMath.mulDiv(
                        positionT0Requested[i],
                        balance0,
                        totalT0Requested
                    );
            }

            if (totalT1Requested > 0) {
                positionT1Amount =
                    FullMath.mulDiv(
                        positionT1Requested[i],
                        balance1,
                        totalT1Requested
                    );
            }

            uint128 liquidity = LiquidityAmounts.getLiquidityForAmounts(
                sqrtPriceX96,
                TickMath.getSqrtRatioAtTick(tempPos.lowerTick[i]),
                TickMath.getSqrtRatioAtTick(tempPos.upperTick[i]),
                positionT0Amount,
                positionT1Amount
            );

            // Create the position inside the pool.
            if (liquidity != 0) {
                pool.mint(address(this), positions.lowerTick[i], positions.upperTick[i], liquidity, "");
            }
        }

        /*
            Check post-mint balances. We need to check that less than 5% of the TO token (i.e. the token we swapped into) remains post-mint.
            Having the right liquidity ratio is extremely valuable, but the main thing we're protecting against here is dynamic data that swaps more than it should. 

            As an example, assume a malicious keeper has flashloaned the uniswap V3 pool into a very bad exchange rate before handling the swap.
            If exchange rate is extremely high token1PerToken0, exploiter will want to swap from token1 to token0 (going the other way just helps the liquidity manager)
            but the positions will be entirely in token1. The check below ensures that no more than 5% of the total contract token0 remains undeposited, a reliable indicator
            that the correct amount of token1 was swapped.

            This combined with the TWAP check makes flashloan exploits extremely difficult for a single keeper.
        */
        // If swapAmount > 0, that means zeroForOne. Otherwise, oneForZero. No overflow checks here because some kind of overflow exploit is both implausible and would just
        // cause a revert.
        if (swapAmount > 0)   {
            //Require that at least 95% of t1 has been deposited (ensuring that swap amount wasn't too great)
            require(getBalance1() < (balance1 * 95) / 100, 'swap');
        } else if (swapAmount != 0)   {
            //Require that at least 95% of t0 has been deposited (ensuring that swap amount wasn't too great)
            require(getBalance0() < (balance0 * 95) / 100, 'swap');
        }
    }

    // Temporary array built to hold the weights of each token in each liquidity position.
    // t0Weights[0] = Token 0 weight in the first liquidity position, multiplied by PRECISION.
    // t1 weight in that position can be calculated using PRECISION * total bin weight - t0 weight.
    function _computePositionAmounts(uint160 sqrtPriceX96)
        internal
        view
        returns (
            uint256[] memory positionT0Requested,
            uint256[] memory positionT1Requested,
            uint256 totalT0Requested,
            uint256 totalT1Requested
        )
    {
        LiquidityPositions memory _positions = positions;

        uint256 positionsLength = _positions.lowerTick.length;
        positionT0Requested = new uint256[](positionsLength);
        positionT1Requested = new uint256[](positionsLength);

        // For each bin, figure out how much of the bin will be in token0, and how much will be in token1. Set weights accordingly--if a bin's weight is 10, and nine tenths of its value
        // will be in token0, then its token0 weight will be 9 and its token1 weight will be 1.
        for (uint256 i; i != positionsLength; ++i) {
            uint160 positionSqrtPriceLower = TickMath.getSqrtRatioAtTick(
                _positions.lowerTick[i]
            );
            uint160 positionSqrtPriceUpper = TickMath.getSqrtRatioAtTick(
                _positions.upperTick[i]
            );

            // For each position, find amount0Wanted and amount1Wanted given a liquidity of PRECISION * relativeWeight.
            (uint256 amount0Wanted, uint256 amount1Wanted) = LiquidityAmounts
                .getAmountsForLiquidity(
                    sqrtPriceX96,
                    positionSqrtPriceLower,
                    positionSqrtPriceUpper,
                    uint128(PRECISION * _positions.relativeWeight[i])
                    // No safecast here--an overflow will lead to an incorrect number, which will either (usually) revert, or cause a harmless liquidity miscalculation.
                );

            // Record amt0Delta and amt1Delta for this position
            positionT0Requested[i] = amount0Wanted;
            positionT1Requested[i] = amount1Wanted;

            // Add amt0Delta and amt1Delta to totalT0Requested and totalT1Requested
            totalT0Requested += amount0Wanted;
            totalT1Requested += amount1Wanted;
        }
    }

    /// @dev Withdraws liquidity from all positions, allocating fees correctly in the process.
    /// @param shares LP shares being withdrawn
    /// @param totalShares total # of LP tokens in the vault
    function _burnAndCollect(
        uint256 shares,
        uint256 totalShares
    )
        internal
        override
        returns (
            uint256 t0,    // token0 earned from burned liquidity + fees. Only includes burned + fees corresponding to LP shares being withdrawn (100% if tend)
            uint256 t1     // token1 earned from burned liquidity + fees
        )
    {
        // First, fetch current positions, Only tend() and withdraw() call this function, and neither uses these positions elsewhere (tend uses updated ones).
        LiquidityPositions memory _positions = positions;

        // For each position, burn() and then withdraw correct amount of liquidity.
        uint256 fees0;
        uint256 fees1;
        uint256 positionCount = _positions.lowerTick.length;
        for (uint256 i; i != positionCount; ++i) { 
            int24 lowerTick = _positions.lowerTick[i];
            int24 upperTick = _positions.upperTick[i];

            uint128 liquidityToBurn;

            // Get position liquidity. If we don't want all of it, here is where we specify how much we want (liquidity * fraction of pool which is being withdrawn)
            // Slightly tortured logic here due to stack management
            {
                (uint128 totalPositionLiquidity, , , , ) = _position(
                    lowerTick,
                    upperTick
                );

                // Shares is always >= totalShares so no risk of uint128 overflow here.
                liquidityToBurn = uint128(FullMath.mulDiv(
                    totalPositionLiquidity,
                    shares,
                    totalShares
                ));
            }

            // amountsOwed are always pulled with liquidity in this contract, so if position liquidity is 0, no need to withdraw anything.
            if (liquidityToBurn != 0)   {
                // Amount burned in each position. Corresponds to amount of liquidity withdrawn (i.e. doesn't include fees).
                (uint256 posBurned0, uint256 posBurned1) = pool.burn(lowerTick, upperTick, liquidityToBurn);

                // Collect all owed tokens including earned fees
                (uint256 collect0, uint256 collect1) = pool.collect(
                    address(this),
                    lowerTick,
                    upperTick,
                    type(uint128).max,
                    type(uint128).max
                );

                /*
                 * Add liquidity burned to t0 and t1--this is already proportional to amt being withdrawn
                 * No need to check for overflow--values come from Uniswap, and a total burn greater than 2^256 - 1 would represent burning more than a token's total supply.
                 * Technically possible given a malicious token, but a malicious token can already steal vault holdings due to the nature of uniswap
                    (just have the vault deposit all tokens, then mint an arbitrary amount of the malicious token and swap for the real token)
                */
                t0 += posBurned0;
                t1 += posBurned1;

                // Fees earned by liquidity inside uniswap = collected - burned. First we allocate some to steer and some to strategist. The remainder is the fees earned by LPs.
                // So after that we add remainder * shares / totalShares, and that gives us t0 and t1 allocated to whatever's being withdrawn.

                // Since burned will never be greater than collected, no need to check for underflow here.
                // Since collect values were originally uint128's, no need to check for overflow either. It would take ~2^128 max additions to overflow.
                fees0 += collect0 - posBurned0;
                fees1 += collect1 - posBurned1;
            }
        }

        // Emit fee info
        emit FeesEarned(
            fees0,
            fees1
        );

        // Update accrued protocol fees
        if (fees0 != 0)   {
            uint256 totalCut0 = FullMath.mulDiv(
                fees0,
                TOTAL_FEE,
                FEE_DIVISOR
            );

            // Subtract fees going to strategist/steer from fees going to vault
            fees0 -= totalCut0;

            // Allocate fee amounts to strategist/steer correctly
            uint256 feesToSteer0 = FullMath.mulDiv(
                totalCut0,
                STEER_FRACTION_OF_FEE,
                FEE_DIVISOR
            );

            // Since steer fees = (totalCut * steerFraction) / 1e4, and steerFraction cannot be greater than 1e4, no need to check for underflow here.
            uint256 feesToStrategist0 = totalCut0 - feesToSteer0;

            // Increment fees. No need to check for overflow fees cannot be greater than ~2^126 and accruedFees are uint256's.
            accruedSteerFees0 += feesToSteer0;
            accruedStrategistFees0 += feesToStrategist0;
        }

        if (fees1 != 0)   {
            uint256 totalCut1 = FullMath.mulDiv(
                fees1,
                TOTAL_FEE,
                FEE_DIVISOR
            );
            fees1 -= totalCut1;

            uint256 feesToSteer1 = FullMath.mulDiv(
                totalCut1,
                STEER_FRACTION_OF_FEE,
                FEE_DIVISOR
            );
            uint256 feesToStrategist1 = totalCut1 - feesToSteer1;
            
            accruedSteerFees1 = accruedSteerFees1.add(feesToSteer1);
            accruedStrategistFees1 = accruedStrategistFees1.add(
                feesToStrategist1
            );
        }
        
        // Add fees earned by burned position to burned amount
        t0 = t0.add(FullMath.mulDiv(
            fees0,
            shares,
            totalShares
        ));

        t1 = t1.add(FullMath.mulDiv(
            fees1,
            shares,
            totalShares
        ));
    }

    /**
     * @notice Calculates the vault's total holdings of token0 and token1 - in
     *      other words, how much of each token the vault would hold if it withdrew
     *      all its liquidity from Uniswap.
     * @notice this function DOES NOT include fees earned since the last burn. 
        To include fees, first poke() and then call getTotalAmounts. There's a function inside the periphery to do so.
     */
    function getTotalAmounts() public override view returns (uint256 total0, uint256 total1)   {
        // Start with tokens currently held inside the vault
        total0 = getBalance0();
        total1 = getBalance1();

        // These include fees to steer and strategist, which we remove before adding them to total0 and total1.
        uint256 totalT0Fees;
        uint256 totalT1Fees;

        LiquidityPositions memory tempPos = positions;
        (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();
        uint256 positionCount = tempPos.lowerTick.length;
        for (uint i; i != positionCount; ++i) {
            // Get calculated amounts of tokens contained within this pool position
            (uint256 amt0, uint256 amt1, uint256 fees0, uint256 fees1) = getPositionAmounts(
                sqrtPriceX96,
                tempPos.lowerTick[i],
                tempPos.upperTick[i]
            );

            // Increment balances
            total0 = total0.add(amt0);
            total1 = total1.add(amt1);

            // Increment fees
            totalT0Fees = totalT0Fees.add(fees0);
            totalT1Fees = totalT1Fees.add(fees1);
        }

        // Subtract protocol fees from position fee earned, then add the (now LP-owned) remaining tokens to total balances
        total0 = total0.add(
            FullMath.mulDiv(
                totalT0Fees,
                ONE_MINUS_FEE,
                FEE_DIVISOR
            )
        );
        total1 = total1.add(
            FullMath.mulDiv(
                totalT1Fees,
                ONE_MINUS_FEE,
                FEE_DIVISOR
            )
        );
    }
}

contracts/tests/UpgradedUniLiquidityMana.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.7.6;
pragma abicoder v2;
//ERC support
import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable-7/token/ERC20/ERC20Upgradeable.sol";
import {SafeERC20} from "@openzeppelin/contracts-7/token/ERC20/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts-7/token/ERC20/IERC20.sol";

//Math
import {Math} from "@openzeppelin/contracts-7/math/Math.sol"; //max(), min(), and average
import {SafeMath} from "@openzeppelin/contracts-7/math/SafeMath.sol";
import {SignedSafeMath} from "@openzeppelin/contracts-7/math/SignedSafeMath.sol";

//Uniswap
import {IUniswapV3Factory} from "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import {IUniswapV3Pool} from "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import {TickMath} from "@uniswap/v3-core/contracts/libraries/TickMath.sol";
import {SqrtPriceMath} from "@uniswap/v3-core/contracts/libraries/SqrtPriceMath.sol";
import {FullMath} from "@uniswap/v3-core/contracts/libraries/FullMath.sol";
import {IUniswapV3MintCallback} from "@uniswap/v3-core/contracts/interfaces/callback/IUniswapV3MintCallback.sol";
import {IUniswapV3SwapCallback} from "@uniswap/v3-core/contracts/interfaces/callback/IUniswapV3SwapCallback.sol";
import {LiquidityAmounts} from "@uniswap/v3-periphery/contracts/libraries/LiquidityAmounts.sol";
import {PositionKey} from "@uniswap/v3-periphery/contracts/libraries/PositionKey.sol";

//Util support
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable-7/utils/PausableUpgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable-7/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable-7/proxy/Initializable.sol";
import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable-7/access/AccessControlUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable-7/utils/PausableUpgradeable.sol";
import {StringsUpgradeable} from "@openzeppelin/contracts-upgradeable-7/utils/StringsUpgradeable.sol";

//Sundry
import "../interfaces/IBareVaultRegistry.sol";

contract UpgradedUniLiquidityManager is
    Initializable,
    ERC20Upgradeable,
    PausableUpgradeable,
    AccessControlUpgradeable,
    IUniswapV3MintCallback,
    IUniswapV3SwapCallback
{
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    using SignedSafeMath for int256;

    //Immutables and constants
    uint256 constant PRECISION = 1e18;

    bytes32 constant STEER_ROLE = keccak256("STEER_ROLE"); //Can collect Steer fees (Governance)
    bytes32 constant ORCHESTRATOR_ROLE = keccak256("ORCHESTRATOR_ROLE"); //Can call update function
    bytes32 constant GOVERNANCE_ROLE = keccak256("GOVERNANCE_ROLE"); //Can call update function    
    //address strategist can collect strategist fees, but is not stored here.

    address vaultRegistry;

    address public token0;
    address public token1;

    //Fee info
    //Fee rates, each multiplied by 10,000 (a TOTAL_FEE of 100 means a 1% cut of total uniswap fees)
    uint24 constant public TOTAL_FEE = 1500; //Total fraction of fees not going towards LPs, multiplied by 10,000
    uint24 constant public STEER_FRACTION_OF_FEE = 6667; //Total fraction of fees going towards Steer (as opposed to going towards strategist)
    uint24 constant public ONE_MINUS_FEE = 1e4 - TOTAL_FEE;

    //Fees currently owed to Steer
    uint256 public accruedSteerFees0;
    uint256 public accruedSteerFees1;

    //Fees currently owed to strategist
    uint256 public accruedStrategistFees0;
    uint256 public accruedStrategistFees1;

    //Max # of LP tokens--users will not be allowed to deposit if that deposit would push LP tokens above max
    uint256 public maxTotalSupply;

    //Constants
    IUniswapV3Factory constant FACTORY =
        IUniswapV3Factory(0x1F98431c8aD98523631AE4a59f267346ea31F984);

    IUniswapV3Pool public pool;

    //For depositing
    int24 maxTickChange; //Roughly corresponds to a 5% diff between current price and twap price
    uint32 twapInterval; //Number of seconds to get the time-weighted average over

    struct LiquidityPositions {
        int24[] lowerTick;
        int24[] upperTick;
        uint16[] relativeWeight;
    }

    LiquidityPositions internal positions;

    modifier onlyRole(bytes32 _role) {
        require(hasRole(_role, msg.sender), 'ROLE');
        _;
    }

    event Snapshot(
        uint160 sqrtPriceX96,
        uint256 totalAmount0,
        uint256 totalAmount1,
        uint256 totalSupply
    );

    event Deposit(
        address indexed sender,
        address indexed to,
        uint256 shares,
        uint256 amount0,
        uint256 amount1
    );

    event Withdraw(
        address indexed sender,
        address indexed to,
        uint256 shares,
        uint256 amount0,
        uint256 amount1
    );

    //Useful if we upgrade to a contract which needs more storage slots
    uint256[100] gap;
    uint256 public num;

    function setNum(uint256 _num) external {
        num = _num;
    }

    function setNumToMultiple(uint256 firstNum, uint256 secondNum) external {
        num = firstNum * secondNum;
    }



    constructor() {}

    /**
     * @dev initializes vault
     * param _orchestrator is address of orchestrator, which has permission to call update.
     * param _params is all other parameters this vault will use.
     * param _tokenName is the name of the LPT of this vault.
     * param _symbol is the symbol of the LPT of this vault.
     * param token0 is address of token0
     * param token1 is address of token1
     * param _FEE is pool fee, how much is charged for a swap
     */

    function initialize(
        address _orchestrator,
        address _steer,
        bytes calldata _params
    ) external initializer {
        (
            address _token0,
            address _token1,
            uint24 _poolFee,
            uint256 _maxTotalSupply,
            int24 _maxTickChange,
            uint32 _twapInterval
        ) = abi.decode(
                _params,
                (
                    address,
                    address,
                    uint24,
                    uint256,
                    int24,
                    uint32
                )
            );

        //context inits
        vaultRegistry = msg.sender;

        token0 = _token0;
        token1 = _token1;

        //pool = IUniswapV3Pool(FACTORY.getPool(address(token0), address(token1), _poolFee));


        //Init the LP token ERC20
        __ERC20_init(
            string(abi.encodePacked("STEER_", ERC20Upgradeable(token0).name(), "-", ERC20Upgradeable(token1).name())),
            string(abi.encodePacked("str_", ERC20Upgradeable(token0).symbol(), "-", ERC20Upgradeable(token1).symbol()))   
            //Just using ERC20 upgradeable because for some reason IERC20 has no name() or symbol()
        );

        //Init everything else
        __Pausable_init();
        __AccessControl_init();

        _setupRole(STEER_ROLE, _steer);
        _setupRole(DEFAULT_ADMIN_ROLE, _steer);
        _setupRole(ORCHESTRATOR_ROLE, _orchestrator);

        //Set max supply
        maxTotalSupply = _maxTotalSupply;

        //Set security params
        require(_maxTickChange >= 0);
        maxTickChange = _maxTickChange;
        twapInterval = _twapInterval;
    }

    /**
     * @dev Deposits tokens in proportion to the vault's current holdings.
     * @dev These tokens sit in the vault and are not used for liquidity on
     * Uniswap until the next rebalance.
     * @param amount0Desired Max amount of token0 to deposit
     * @param amount1Desired Max amount of token1 to deposit
     * @param amount0Min Revert if resulting `amount0` is less than this
     * @param amount1Min Revert if resulting `amount1` is less than this
     * @param to Recipient of shares
     * @return shares Number of shares minted
     * @return amount0 Amount of token0 deposited
     * @return amount1 Amount of token1 deposited
 
     * @dev function requirements:  
        Either amount0Desired or amount1Desired must be > 0 or it will revert with 'CROSS'
     */
    function deposit(
        uint256 amount0Desired,
        uint256 amount1Desired,
